// GetNextUserAgent returns the next user agent in round-robin fashion
func GetNextUserAgent() string {
	userAgentMutex.Lock()
	defer userAgentMutex.Unlock()

	ua := userAgents[userAgentIndex]
	userAgentIndex = (userAgentIndex + 1) % len(userAgents)
	return ua
}
